mesh(x,y,D)

%% Calculate angle alpha, i.e., the seabed slope
for i=2:5
    for j=1:6
        tan_alpha(j,i-1) = (D(j,i)-D(j,i-1))/1835;
    end
end
alpha = atan(tan_alpha);   % When tan(x) is very small, it can be approximated as x

%% Import Known Conditions
theta = deg2rad(120);  % Opening angle, same as previous value
beta = deg2rad(0);     % Based on experiments in Problem 3, it is concluded that regardless of alpha, the beta satisfying the shortest line is 0 (i.e., North-South direction)
eta_max = 0.2;

%% Calculate coverage range W, initial line starting position a, minimum spacing d_min satisfying eta (<=20%), and spacing d used for target data
for i=2:5
    for j=1:6
        W(j,i-1) = 2*D(j,i)/cos(theta/2-alpha(j,i-1))/(1/sin(theta/2)-tan(alpha(j,i-1))/cos(theta/2));  % Determine coverage range for each point
        a(j,i-1) = W(j,i-1)*cos(beta)*(cos(alpha(j,i-1))+sin(alpha(j,i-1))*tan(theta/2));                % Determine the starting point of the initial family of lines
        d_min(j,i-1) = (1-eta_max)*(cos(alpha(j,i-1))*W(j,i-1));   % Calculate the range of d: minimum value
    end
end
d0 = mean(mean(d_min)); % Average value
d1 = abs(d_min-d0);     % Find a value in d_min closest to the average to determine the d for subsequent calculations
x0 = 1:4;
y0 = 1:6;
[x0,y0] = find(d1==min(min(d1)));
d_aim = d_min(x0,y0);
alpha0 = alpha(x0,y0);  % After finding d, take the alpha value from the same coordinates
a1 = a(x0,y0);
n = floor((5*1852-a1)/d_aim)+1;

%% Calculate shortest ray distance
sum_shortdistance = n*5*1852;

%% Total length where overlap rate > 20%, and percentage of missed sea area relative to the total area
sum_long = 0;
sum_area = 0;
m=0;
for i=2:200
    for j=2:250
        W0(j,i) = 2*D0(j,i)/cos(theta/2-alpha0)/(1/sin(theta/2)-tan(alpha0)/cos(theta/2));  % Determine coverage range for each point
        eta(j,i) = 1-d_aim/(W0(j,i)*cos(alpha0));
